int Q_strncasecmp (char *s1, char *s2, int n)
{
	int		c1, c2;

	if(!s1 || !s2)
		return -1;			// one or both strings are NULL

	do
	{
		c1 = *s1++;
		c2 = *s2++;

		if (!n--)
			return 0;		// strings are equal until end point

		if (c1 != c2)
		{
			if (c1 >= 'a' && c1 <= 'z')
				c1 -= ('a' - 'A');
			if (c2 >= 'a' && c2 <= 'z')
				c2 -= ('a' - 'A');
			if (c1 != c2)
				return -1;		// strings not equal
		}
	} while (c1);

	return 0;		// strings are equal
}


int Q_strcasecmp (char *s1, char *s2)
{
	return Q_strncasecmp (s1, s2, 99999);
}


int Q_strnicmp (const char *s1, const char *s2, int n)
{
	int		c1, c2;

	if (!s1)
	{
		if (!s2)
			return 0;
		else
			return -1;
	}
	else if (!s2)
		return 1;

	do
	{
		c1 = *s1++;
		c2 = *s2++;

		if (!n--)
			return 0;	// strings are equal until end point

		if (c1 != c2)
		{
			if (c1 >= 'a' && c1 <= 'z')
				c1 -= ('a' - 'A');
			if (c2 >= 'a' && c2 <= 'z')
				c2 -= ('a' - 'A');

			if (c1 != c2)
				return c1 < c2 ? -1 : 1;
		}
	} while (c1);

	return 0;			// strings are equal
}


int Q_stricmp (const char *s1, const char *s2)
{
	return Q_strnicmp (s1, s2, 99999);
}

void Con_Linefeed ()
{
	con.x = 0;
	if (con.display == con.current)
		con.display++;
	con.current++;
	memset (&con.text[(con.current%con.totallines)*con.linewidth], ' ', con.linewidth);
	memset (&con.color[(con.current%con.totallines)*con.linewidth], 7, con.linewidth);
}


/*
================
Con_Print

Handles cursor positioning, line wrapping, etc
All console printing must go through this in order to be logged to disk
If no console is visible, the text will appear at the top of the game window
================
*/
void Con_Print (char *txt)
{
	int		y;
	int		c, l;
	char	*cc;
	static int	cr;
///	int		mask;
	char	cindex;

	if (!con.initialized)
		return;

	if (txt[0] == 1 || txt[0] == 2)
	{
///		mask = 128;		// go to colored text
		cindex = 2;
		txt++;
	}
///	else
///		mask = 0;

	cindex = 7;
	while ( 1 )
	{
povtor:	if (!(c = *txt))
			break;

		if (Q_IsColorString(txt))
		{
			cindex = ColorIndex(txt[1]);
			txt+=2;
			goto povtor;
		}

		// count word length
		int ll=0;
		for (l=0 ; l< con.linewidth ; l++)
		{
			cc = &txt[l];
			if (Q_IsColorString(cc))
				l+=2;
			if (txt[l] <= ' ')
				break;
			ll++;
		}

	// word wrap
		if (ll != con.linewidth && (con.x + ll > con.linewidth) )
			con.x = 0;

		txt++;

		if (cr)
		{
			con.current--;
			cr = false;
		}

		if (!con.x)
		{
			Con_Linefeed ();
		// mark time for transparent overlay
			if (con.current >= 0)
				con.times[con.current % NUM_CON_TIMES] = cls.realtime;
		}

		switch (c)
		{
		case '\n':
			con.x = 0;
			break;

		case '\r':
			con.x = 0;
			cr = 1;
			break;

		default:	// display character and advance
			y = con.current % con.totallines;
			con.text[y*con.linewidth+con.x] = c;/// | mask | con.ormask;
			con.color[y*con.linewidth+con.x] = cindex;
			con.x++;
			if (con.x >= con.linewidth)
				con.x = 0;
			break;
		}

	}
}


