/*
================
Con_DrawInput

The input line scrolls horizontally if typing goes beyond the right edge
================
*/
void Con_DrawInput ()
{
	int		y, i;
	char	*text;

	if (cls.key_dest == key_menu)
		return;
	if (cls.key_dest != key_console && cls.state == ca_active)
		return;		// don't draw anything (always draw if not active)

	text = key_lines[edit_line];

// add the cursor frame
	text[key_linepos] = 10+((int)(cls.realtime>>8)&1);

// fill out remainder with spaces
	for (i=key_linepos+1 ; i< con.linewidth ; i++)
		text[i] = ' ';

//	prestep if horizontally scrolling
	if (key_linepos >= con.linewidth)
		text += 1 + key_linepos - con.linewidth;

	// draw it
	y = con.vislines - 44;
	for (i=0 ; i<con.linewidth ; i++)
		Draw_Char16 ( (i+1)<<4, y, text[i]);

// remove cursor
	key_lines[edit_line][key_linepos] = 0;
}


/*
================
Con_DrawConsole

Draws the console with the solid background
================
*/
bool Con_DrawConsole (float frac)
{
	int				i, x, y, n, j;
	int				rows;
	char			*text, *colors;
	int				row;
	int				lines;
	char			version[64];
	char			dlbar[1024];

	lines = viddef.height * frac;
	if (lines <= 0)
		return true;

	if (lines > viddef.height)
		lines = viddef.height;

// draw the background
	if (!Draw_Console (-viddef.height+lines, viddef.width, viddef.height))
		return false;
	SCR_AddDirtyPoint (0,0);
	SCR_AddDirtyPoint (viddef.width-1,lines-1);

///	Com_sprintf (version, sizeof(version), "Version %4.2f", VERSION);
	Com_sprintf (version, sizeof(version), "Release 1.45 (%s)", __DATE__);
	int vlen = strlen(version);
	for (x=0 ; x<vlen ; x++)
		Draw_Char16 (viddef.width-8-(vlen-x)*16, 8, 128 + version[x] );

// draw the text
	con.vislines = lines;
// adjust to 640x480
	lines = lines>>1;

	rows = (lines-22)>>3;		// rows of text to draw
	y = lines - 30;

// draw from the bottom up
	if (con.display != con.current)
	{
	// draw arrows to show the buffer is backscrolled
		for (x=0 ; x<con.linewidth ; x+=4)
			Draw_Char16 ( (x+1)<<4, y<<1, '^');
		y -= 8;
		rows--;
	}

	row = con.display;
	gl_TexEnv( GL_MODULATE );
	for (i=0 ; i<rows ; i++, y-=8, row--)
	{
		if (row < 0)
			break;
		if (con.current - row >= con.totallines)
			break;		// past scrollback wrap point

		text = con.text + (row % con.totallines)*con.linewidth;
		colors = con.color + (row % con.totallines)*con.linewidth;

		for (x=0 ; x<con.linewidth ; x++)
			Draw_Char16_Colored ( (x+1)<<4, y<<1, text[x], colors[x]);
	}
	GL_Color3f(1,1,1);
	gl_TexEnv( GL_REPLACE );

//ZOID
	// draw the download bar
	// figure out width
	if (cls.download)
	{
		if ((text = strrchr(cls.downloadname, '/')) != NULL)
			text++;
		else if ((text = strrchr(cls.downloadname, '\\')) != NULL)		// berserker: damn path name
			text++;
		else
			text = cls.downloadname;

		x = con.linewidth - ((con.linewidth * 7) / 40);
		y = x - strlen(text) - 8;
		i = con.linewidth/3;
		if (strlen(text) > i)
		{
			y = x - i - 11;
			strncpy(dlbar, text, i);
			dlbar[i] = 0;
			strcat(dlbar, "...");
		} else
			strcpy(dlbar, text);
		strcat(dlbar, ": ");
		i = strlen(dlbar);
		dlbar[i++] = '\x80';
		// where's the dot go?
		if (cls.downloadpercent == 0)
			n = 0;
		else
			n = y * cls.downloadpercent / 100;

		for (j = 0; j < y; j++)
			if (j == n)
				dlbar[i++] = '\x83';
			else
				dlbar[i++] = '\x81';
		dlbar[i++] = '\x82';
		dlbar[i] = 0;

		sprintf(dlbar + strlen(dlbar), " %02d%%", cls.downloadpercent);

		// draw it
		y = con.vislines-24;
		for (i = 0; i < strlen(dlbar); i++)
			Draw_Char16 ( (i+1)<<4, y, dlbar[i]);
	}
//ZOID

// draw the input prompt, user text, and cursor if desired
	Con_DrawInput ();

	return true;
}


