/*
=========
Q_SortStricmp
=========
*/
int Q_SortStricmp (const void *a, const void *b)
{
	char	*aa, *bb;

	aa = *(char **)a;
	bb = *(char **)b;

	return Q_stricmp(aa, bb);
}

bool	one_match;
char	retval[256];
char *Cmd_CompleteCommand (char *partial)
{
	cmd_function_t	*cmd;
	cvar_t			*cvar;
	cmdalias_t		*a;
	int				len, i, o, p;
	bool			diff;
	unsigned		hash = Com_HashKey(partial);
	char			*pmatch[4096];

	len = strlen(partial);

	if (!len)
		return NULL;

// check exact match
	one_match = true;

	for (cmd=cmd_functions ; cmd ; cmd=cmd->next)
		if (hash == cmd->hash)
			if (!Q_strcasecmp(cmd->name, partial))
				return cmd->name;

	for (cvar=cvar_vars ; cvar ; cvar=cvar->next)
		if (hash == cvar->hash)
			if (!Q_strcasecmp(cvar->name, partial))
				return cvar->name;

	for (a=cmd_alias ; a ; a=a->next)
		if (hash == a->hash)
			if (!Q_strcasecmp(a->name, partial))
				return a->name;

// check partial match
	i=0;

	for (cmd=cmd_functions ; cmd ; cmd=cmd->next)
		if (!Q_strnicmp(partial, cmd->name, len))
		{
			if (i==4096)
				break;
			pmatch[i++] = cmd->name;
		}

	for (cvar=cvar_vars ; cvar ; cvar=cvar->next)
		if (!Q_strnicmp(partial, cvar->name, len))
		{
			if (i==4096)
				break;
			pmatch[i++] = cvar->name;
		}

	for (a=cmd_alias ; a ; a=a->next)
		if (!Q_strnicmp(partial, a->name, len))
		{
			if (i==4096)
				break;
			pmatch[i++] = a->name;
		}

	if (i)
	{
		char *cvstr;

		if (i == 1)
		{
			cvstr = Cvar_VariableString(pmatch[0]);
			if (cvstr && cvstr[0])
				Com_Printf("  %s = %s\n", pmatch[0], cvstr);
			else
				Com_Printf("  %s\n", pmatch[0]);

			Com_sprintf (retval, sizeof(retval), pmatch[0]);
			return retval;
		}

		// sort alphabetically
		qsort(pmatch, i, sizeof(char *), (cmp_t *)Q_SortStricmp);

		Com_Printf("Matches for '%s':\n", partial);
		for (o=0; o<i; o++)
		{
			cvstr = Cvar_VariableString(pmatch[o]);
			if (cvstr && cvstr[0])
				Com_Printf("  %s = %s\n", pmatch[o], cvstr);
			else
				Com_Printf("  %s\n", pmatch[o]);
		}

		retval[0] = 0;
		p = 0;
		diff = false;

		while (!diff && p < 256)
		{
			retval[p] = pmatch[0][p];

			for (o=0; o<i; o++)
			{
				if(p > strlen(pmatch[o]))
					continue;

				if((retval[p]|0x20) != (pmatch[o][p]|0x20))
				{
					retval[p] = 0;
					diff = false;
				}
			}
			p++;
		}
		one_match = false;
		Com_Printf("%i matches\n\n",i);
		return retval;
	}

	return NULL;
}


// Berserker's fix: позволим автозаполнять игнорируя ' ' и ';'
void CompleteCommand ()
{
	unsigned	cnt, stamp;
	char		*cmd, *s, *last;
	bool		first = true;
	bool		wasToken=false;
	bool		block=false;

	s = key_lines[edit_line]+1;
	if (*s == '\\' || *s == '/')
	{
		s++;
		stamp=cnt=2;
	}
	else
		stamp=cnt=1;

	last=s;
	while (1)
	{
		if (*s == 0)
			break;
		if (*s == ';')
		{
			block=wasToken=first=false;
			s++;
			last=s;
			stamp=cnt;
		}
		else if (*s == ' ')
		{
			s++;
			last=s;
			if (wasToken)
				block=true;
		}
		else
		{
			s++;
			wasToken=true;
		}
		cnt++;
	}

	if (!block)
	{
		cmd = Cmd_CompleteCommand (last);
		if (cmd)
		{
			if (first)
			{
				key_lines[edit_line][1] = '/';
				strcpy (key_lines[edit_line]+2, cmd);
				key_linepos = strlen(cmd)+2;
				if (one_match)
					key_lines[edit_line][key_linepos++] = ' ';
				key_lines[edit_line][key_linepos] = 0;
			}
			else
			{	// заполнение не первой команды
				if (strlen(key_lines[edit_line])+stamp+1+strlen(cmd)<MAXCMDLINE)
				{
					strcpy (key_lines[edit_line]+stamp+1, cmd);
					key_linepos = strlen(key_lines[edit_line]);
					if (one_match)
						key_lines[edit_line][key_linepos++] = ' ';
					key_lines[edit_line][key_linepos] = 0;
				}
			}
		}
	}
}


/*
====================
Key_Console

Interactive line editing and console scrollback
====================
*/
void Key_Console (int key)
{
	switch ( key )
	{
	case K_KP_SLASH:
		key = '/';
		break;
	case K_KP_MINUS:
		key = '-';
		break;
	case K_KP_PLUS:
		key = '+';
		break;
	case K_KP_HOME:
		key = '7';
		break;
	case K_KP_UPARROW:
		key = '8';
		break;
	case K_KP_PGUP:
		key = '9';
		break;
	case K_KP_LEFTARROW:
		key = '4';
		break;
	case K_KP_5:
		key = '5';
		break;
	case K_KP_RIGHTARROW:
		key = '6';
		break;
	case K_KP_END:
		key = '1';
		break;
	case K_KP_DOWNARROW:
		key = '2';
		break;
	case K_KP_PGDN:
		key = '3';
		break;
	case K_KP_INS:
		key = '0';
		break;
	case K_KP_DEL:
		key = '.';
		break;
	}

	if ( ( toupper( key ) == 'V' && keydown[K_CTRL] ) ||
		 ( ( ( key == K_INS ) || ( key == K_KP_INS ) ) && keydown[K_SHIFT] ) )
	{
		char *cbd;

		if ( cbd = SDL_GetClipboardText() )
		{
			int i;

			strtok( cbd, "\n\r\b" );

			i = strlen( cbd );
			//r1: save byte for null terminator!!
			if ( i + key_linepos >= MAXCMDLINE - 1)
				i= MAXCMDLINE - key_linepos - 1;

			if ( i > 0 )
			{
				cbd[i]=0;
				strcat( key_lines[edit_line], cbd );
				key_linepos += i;
			}
			SDL_free( cbd );
		}

		return;
	}

	if ( key == 'l' )
	{
		if ( keydown[K_CTRL] )
		{
			Cbuf_AddText ("clear\n");
			return;
		}
	}

	if ( key == K_ENTER || key == K_KP_ENTER )
	{	// backslash text are commands, else chat
		if (key_lines[edit_line][1] == '\\' || key_lines[edit_line][1] == '/')
			Cbuf_AddText (key_lines[edit_line]+2);	// skip the >
		else
			Cbuf_AddText (key_lines[edit_line]+1);	// valid command

		Cbuf_AddText ("\n");
		Com_Printf ("%s\n",key_lines[edit_line]);
		edit_line = (edit_line + 1) & 31;
		history_line = edit_line;
		key_lines[edit_line][0] = ']';
		key_linepos = 1;
		if (cls.state == ca_disconnected)
			SCR_UpdateScreen ();	// force an update, because the command
									// may take some time
		return;
	}

	if (key == K_TAB)
	{	// command completion
		CompleteCommand ();
		return;
	}

	if ( ( key == K_BACKSPACE ) || ( key == K_LEFTARROW ) || ( key == K_KP_LEFTARROW ) || ( ( key == 'h' ) && ( keydown[K_CTRL] ) ) )
	{
		if (key_linepos > 1)
			key_linepos--;
		return;
	}

	if ( ( key == K_UPARROW ) || ( key == K_KP_UPARROW ) ||
		 ( ( key == 'p' ) && keydown[K_CTRL] ) )
	{
		do
		{
			history_line = (history_line - 1) & 31;
		} while (history_line != edit_line
				&& !key_lines[history_line][1]);
		if (history_line == edit_line)
			history_line = (edit_line+1)&31;
		strcpy(key_lines[edit_line], key_lines[history_line]);
		key_linepos = strlen(key_lines[edit_line]);
		return;
	}

	if ( ( key == K_DOWNARROW ) || ( key == K_KP_DOWNARROW ) ||
		 ( ( key == 'n' ) && keydown[K_CTRL] ) )
	{
		if (history_line == edit_line) return;
		do
		{
			history_line = (history_line + 1) & 31;
		}
		while (history_line != edit_line
			&& !key_lines[history_line][1]);
		if (history_line == edit_line)
		{
			key_lines[edit_line][0] = ']';
			key_linepos = 1;
		}
		else
		{
			strcpy(key_lines[edit_line], key_lines[history_line]);
			key_linepos = strlen(key_lines[edit_line]);
		}
		return;
	}

	if (key == K_PGUP || key == K_KP_PGUP || key == K_MWHEELUP )
	{
		con.display -= 2;
		return;
	}

	if (key == K_PGDN || key == K_KP_PGDN || key == K_MWHEELDOWN )
	{
		con.display += 2;
		if (con.display > con.current)
			con.display = con.current;
		return;
	}

	if (key == K_HOME || key == K_KP_HOME )
	{
		con.display = con.current - con.totallines + 10;
		return;
	}

	if (key == K_END || key == K_KP_END )
	{
		con.display = con.current;
		return;
	}

	if (key < 32 || key > 127)
		return;	// non printable

	if (key_linepos < MAXCMDLINE-1)
	{
		key_lines[edit_line][key_linepos] = key;
		key_linepos++;
		key_lines[edit_line][key_linepos] = 0;
	}
}


