/*
==============
COM_Parse
Parse a token out of a string
==============
*/
char *COM_Parse (char **data_p)
{
	int		c;
	char	*data;

	data = *data_p;
	com_token_len = 0;
	com_token[0] = 0;

	if (!data)
	{
		*data_p = NULL;
		return "";
	}

// skip whitespace
skipwhite:
	while ( (c = *data) <= ' ')
	{
		if (c == -96)
			break;
		if (c == 0)
		{
			*data_p = NULL;
			return "";
		}
		data++;
	}

// skip // comments
	if (c=='/' && data[1] == '/')
	{
		while (*data && *data != '\n')
			data++;
		goto skipwhite;
	}

// handle quoted strings specially
	if (c == '\"')
	{
		data++;
		while (1)
		{
			c = *data++;
			if (c=='\"' || !c)
			{
				com_token[com_token_len] = 0;
				*data_p = data;
				return com_token;
			}
			if (com_token_len < MAX_TOKEN_CHARS)
			{
				com_token[com_token_len] = c;
				com_token_len++;
			}
		}
	}

// parse a regular word
	do
	{
		if (com_token_len < MAX_TOKEN_CHARS)
		{
			com_token[com_token_len] = c;
			com_token_len++;
		}
		data++;
		c = *data;
	} while (c>32 || c==-96);

	if (com_token_len == MAX_TOKEN_CHARS)
	{
//		Com_Printf ("Token exceeded %i chars, discarded.\n", MAX_TOKEN_CHARS);
		com_token_len = 0;
	}
	com_token[com_token_len] = 0;

	*data_p = data;
	return com_token;
}


char *Cmd_MacroExpandString (char *text)
{
	int		i, j, count, len, k;
	bool	inquote;
	char	*scan;
	static	char	expanded[MAX_STRING_CHARS];
	char	temporary[MAX_STRING_CHARS];
	char	*token, *start;

	inquote = false;
	scan = text;

	len = strlen (scan);
	if (len >= MAX_STRING_CHARS)
	{
		Com_Printf ("^3Line exceeded %i chars, discarded.\n", MAX_STRING_CHARS);
		return NULL;
	}

	count = 0;

	for (i=0 ; i<len ; i++)
	{
		if (scan[i] == '"')
			inquote ^= 1;
		if (inquote)
			continue;	// don't expand inside quotes
		if (scan[i] != '$')
			continue;
		// scan out the complete macro
		start = scan+i+1;
		token = COM_Parse (&start);
		if (!start)
			continue;

		token = Cvar_VariableString (token);

		j = strlen(token);
		len += j;
		if (len >= MAX_STRING_CHARS)
		{
			Com_Printf ("^3Expanded line exceeded %i chars, discarded.\n", MAX_STRING_CHARS);
			return NULL;
		}

		strncpy (temporary, scan, i);
		strcpy (temporary+i, token);
		strcpy (temporary+i+j, start);

		/// Berserker: внутри токена заменяем все пробелы на 0xA0 (-96), чтобы считать токен единым словом
		for (k=i; k<i+j; k++)
			if (temporary[k]==0x20)		// if SPACE in token,
				temporary[k]=-96;		// replace SPACE with (SPACE+0x80 = -96)

		strcpy (expanded, temporary);
		scan = expanded;
		i--;

		if (++count == 100)
		{
			Com_Printf ("^3Macro expansion loop, discarded.\n");
			return NULL;
		}
	}

	if (inquote)
	{
		Com_Printf ("^3Line has unmatched quote, discarded.\n");
		return NULL;
	}

	return scan;
}


/*
============
Cmd_TokenizeString

Parses the given string into command line tokens.
$Cvars will be expanded unless they are in a quoted token
============
*/
void Cmd_TokenizeString (char *text, bool macroExpand)
{
	int		i, k, l;
	char	*com_token;

// clear the args from the last string
	for (i=0 ; i<cmd_argc ; i++)
		Z_Free (cmd_argv[i]);

	cmd_argc = 0;
	cmd_args[0] = 0;

	// macro expand the text
	if (macroExpand)
		text = Cmd_MacroExpandString (text);
	if (!text)
		return;

	while (1)
	{
// skip whitespace up to a /n
		while (*text && *text <= ' ' && *text != '\n' && *text != -96)
			text++;

		if (*text == '\n')
		{	// a newline separates commands in the buffer
			text++;
			break;
		}

		if (!*text)
			return;

		// set cmd_args to everything after the first arg
		if (cmd_argc == 1)
		{
			strcpy (cmd_args, text);
			/// Berserker: теперь восстановим токен, заменив все 0xA0 (-96) на пробелы
			if (macroExpand)
			{
				l = strlen(cmd_args);
				for (k=0; k<l; k++)
					if (cmd_args[k]==-96)
						cmd_args[k]=0x20;
			}

			// strip off any trailing whitespace
			l = strlen(cmd_args) - 1;
			for ( ; l >= 0 ; l--)
				if (cmd_args[l] <= ' ')
					cmd_args[l] = 0;
				else
					break;
		}

		com_token = COM_Parse (&text);
		if (!text)
			return;

		if (cmd_argc < MAX_STRING_TOKENS)
		{
			char *str;
			l = strlen(com_token);
			str = cmd_argv[cmd_argc] = (char*) Z_Malloc (l+1, true);
			strcpy (str, com_token);
			/// Berserker: теперь восстановим токен, заменив все 0xA0 (-96) на пробелы
			if (macroExpand)
			{
				for (k=0; k<l; k++)
					if (str[k]==-96)
						str[k]=0x20;
			}
			cmd_argc++;
		}
	}
}


/*
============
Cbuf_InsertText

Adds command text immediately after the current command
Adds a \n to the text
FIXME: actually change the command buffer to do less copying
============
*/
void Cbuf_InsertText (char *text)
{
	char	*temp;
	int		templen;

// copy off any commands still remaining in the exec buffer
	templen = cmd_text.cursize;
	if (templen)
	{
		temp = (char*) Z_Malloc (templen, true);
		memcpy (temp, cmd_text.data, templen);
		SZ_Clear (&cmd_text);
	}
	else
		temp = NULL;	// shut up compiler

// add the entire text of the file
	Cbuf_AddText (text);

// add the copied off data
	if (templen)
	{
		SZ_Write (&cmd_text, temp, templen);
		Z_Free (temp);
	}
}


static bool Cvar_InfoValidate (char *s)
{
	if (strchr (s, '\\'))
		return false;
	if (strchr (s, '\"'))
		return false;
	if (strchr (s, ';'))
		return false;
	return true;
}




/*
============
Cvar_Get

If the variable already exists, the value will not be set
The flags will be or'ed in if the variable exists.
============
*/
cvar_t *Cvar_Get (char *var_name, char *var_value, int flags)
{
	cvar_t	*var;

	if (flags & (CVAR_USERINFO | CVAR_SERVERINFO))
	{
		if (!Cvar_InfoValidate (var_name))
		{
			Com_Printf("^1invalid info cvar name\n");
			return NULL;
		}
	}

	var = Cvar_FindVar (var_name);
	if (var)
	{
		if(var->defaultString)
		{
			Z_Free(var->defaultString);
			var->defaultString = NULL;
		}
		if(var_value)
		{
			var->defaultString = (char*) Z_Malloc (strlen(var_value)+1, true);
			var->defaultString = CopyString(var_value);
		}
		var->flags |= flags;
		return var;
	}

	if (!var_value)
		return NULL;

	if (flags & (CVAR_USERINFO | CVAR_SERVERINFO))
	{
		if (!Cvar_InfoValidate (var_value))
		{
			Com_Printf("^1invalid info cvar value\n");
			return NULL;
		}
	}

	var = (struct cvar_s *) Z_Malloc (sizeof(*var), true);
	var->name = CopyString (var_name);
	var->string = CopyString (var_value);
	var->defaultString = (char*) Z_Malloc (strlen(var_value)+1, true);
	var->defaultString = CopyString(var_value);
	var->modified = true;
	var->value = atof (var->string);
	var->hash = Com_HashKey(var_name);

	// link the variable in
	var->next = cvar_vars;
	cvar_vars = var;

	var->flags = flags;
	var->help = NULL;

	return var;
}


cvar_t *Cvar_FullSet (char *var_name, char *value, int flags)
{
	cvar_t	*var;

	var = Cvar_FindVar (var_name);
	if (!var)
		return Cvar_Get (var_name, value, flags);	// create it

	var->modified = true;

	if (var->flags & CVAR_USERINFO)
		var->need_send = userinfo_modified = true;	// transmit at next oportunity

	Z_Free (var->string);	// free the old value string

	var->string = CopyString(value);
	var->value = atof (var->string);
	var->flags = flags;

	return var;
}


pack_t *FS_LoadPackFile (char *packfile, bool isPK2)
{
	pack_t			*pack;
	FILE			*packhandle;
	int				numpackfiles;
	packfile_t		*newfiles;

	packhandle = FS_Fopen(packfile, "rb");
	if (!packhandle)
		return NULL;

	if (!isPK2)
	{
		int				i;
		dpackheader_t	header;
		dpackfile_t		info[MAX_FILES_IN_PACK];

		fread (&header, 1, sizeof(header), packhandle);
		if (LittleLong(header.ident) != IDPAKHEADER)
			Com_Error (ERR_FATAL, "%s is not a packfile", packfile);
		header.dirofs = LittleLong (header.dirofs);
		header.dirlen = LittleLong (header.dirlen);

		numpackfiles = header.dirlen / sizeof(dpackfile_t);

		if (numpackfiles > MAX_FILES_IN_PACK)
			Com_Error (ERR_FATAL, "%s has %i files", packfile, numpackfiles);

		newfiles = (packfile_t*)Z_Malloc (numpackfiles * sizeof(packfile_t), true);

		fseek (packhandle, header.dirofs, SEEK_SET);
		fread (info, 1, header.dirlen, packhandle);

		// parse the directory
		for (i=0 ; i<numpackfiles ; i++)
		{
			strcpy (newfiles[i].name, info[i].name);
			newfiles[i].hash = Com_HashKey(newfiles[i].name);
			newfiles[i].filepos = LittleLong(info[i].filepos);
			newfiles[i].filelen = LittleLong(info[i].filelen);
		}
		Com_Printf ("Added packfile %s (%i files)\n", packfile, numpackfiles);
	}
	else
	{
		numpackfiles = 0;
		newfiles = NULL;
		Com_Printf ("Added packfile %s", packfile);

		int	i, j = -1;
		for(i=0; i<MAX_PAKS; i++)
		{
			if(ZipCache[i].uf)
			{
				if (!Q_strcasecmp(ZipCache[i].pak_name, packfile))
					break;
			}
			else
			{
				if (j == -1)
				{
					j = i;
					break;
				}
			}
		}
		if (j != -1)
		{
			Com_Printf (", caching... ");
			strcpy(&ZipCache[j].pak_name[0], packfile);
			if (!PackFileOpen (&ZipCache[j]))
				Com_Error(ERR_FATAL, "Error opening pk2-file: %s", packfile);
			Com_Printf ("Done (%i files)", PackFileGetFilesNumber(&ZipCache[j]));
		}
		Com_Printf ("\n");
	}

	pack = (pack_t *) Z_Malloc (sizeof (pack_t), true);
	pack->isPK2 = isPK2;
	strcpy (pack->filename, packfile);
	pack->handle = packhandle;
	pack->numfiles = numpackfiles;
	pack->files = newfiles;

	return pack;
}


/*
 =================
 Q_GlobMatchAfterStar

 Like Q_GlobMatch, but match pattern against any final segment of text
 =================
*/
bool Q_GlobMatch (const char *pattern, const char *text, bool caseSensitive);
static bool Q_GlobMatchAfterStar (const char *pattern, const char *text, bool caseSensitive)
{
	const char	*p = pattern;
	const char	*t = text;
	char		c1, c2;

	while ((c1 = *p++) == '?' || c1 == '*')
	{
		if (c1 == '?' && *t++ == '\0')
			return false;
	}

	if (c1 == '\0')
		return true;

	if (c1 == '\\')
		c2 = *p;
	else
		c2 = c1;

	while (1)
	{
		if (caseSensitive)
		{
			if (c1 == '[' || *t == c2)
			{
				if (Q_GlobMatch(p - 1, t, caseSensitive))
					return true;
			}
		}
		else
		{
			if (c1 == '[' || tolower(*t) == tolower(c2))
			{
				if (Q_GlobMatch(p - 1, t, caseSensitive))
					return true;
			}
		}

		if (*t++ == '\0')
			return false;
	}
}

/*
 =================
 Q_GlobMatch

 Matches the pattern against text.
 Returns true if matches, false otherwise.

 A match means the entire text is used up in matching.

 In the pattern string, '*' matches any sequence of characters, '?'
 matches any character, '[SET]' matches any character in the specified
 set, '[!SET]' matches any character not in the specified set.

 A set is composed of characters or ranges. A range looks like character
 hyphen character (as in 0-9 or A-Z).
 [0-9a-zA-Z_] is the set of characters allowed in C identifiers.
 Any other character in the pattern must be matched exactly.

 To suppress the special syntactic significance of any of '[]*?!-\', and
 match the character exactly, precede it with a '\'.
 =================
*/
bool Q_GlobMatch (const char *pattern, const char *text, bool caseSensitive)
{
	const char	*p = pattern;
	const char	*t = text;
	char		c1, c2, start, end;
	bool		invert;

	while ((c1 = *p++) != '\0')
	{
		switch (c1)
		{
		case '?':
			if (*t == '\0')
				return false;
			else
				++t;

			break;
		case '\\':
			if (caseSensitive)
			{
				if (*p++ != *t++)
					return false;
			}
			else
			{
				if (tolower(*p++) != tolower(*t++))
					return false;
			}

			break;
		case '*':
			return Q_GlobMatchAfterStar(p, t, caseSensitive);

		case '[':
			c2 = *t++;
			if (!c2)
				return false;

			invert = (*p == '!');
			if (invert)
				p++;

			c1 = *p++;
			while (1)
			{
				start = c1;
				end = c1;

				if (c1 == '\\')
				{
					start = *p++;
					end = start;
				}
				if (c1 == '\0')
					return false;

				c1 = *p++;
				if (c1 == '-' && *p != ']')
				{
					end = *p++;
					if (end == '\\')
						end = *p++;
					if (end == '\0')
						return false;

					c1 = *p++;
				}

				if (caseSensitive)
				{
					if (c2 >= start && c2 <= end)
						goto match;
				}
				else
				{
					if (tolower(c2) >= tolower(start) && tolower(c2) <= tolower(end))
						goto match;
				}

				if (c1 == ']')
					break;
			}

			if (!invert)
				return false;

			break;

match:
			while (c1 != ']')
			{
				if (c1 == '\0')
					return false;

				c1 = *p++;
				if (c1 == '\0')
					return false;
				else if (c1 == '\\')
					++p;
			}

			if (invert)
				return false;

			break;

		default:
			if (caseSensitive)
			{
				if (c1 != *t++)
					return false;
			}
			else
			{
				if (tolower(c1) != tolower(*t++))
					return false;
			}

			break;
		}
	}

	return (*t == '\0');
}


