/*
===================
Cmd_ForwardToServer

adds the current command line as a clc_stringcmd to the client message.
things like godmode, noclip, etc, are commands directed to the server,
so when they are typed in at the console, they will need to be forwarded.
===================
*/
void Cmd_ForwardToServer ()
{
	char	*cmd;

	cmd = Cmd_Argv(0);
	if (cls.state <= ca_connected || *cmd == '-' || *cmd == '+')
	{
		Com_Printf ("^1Unknown command \"%s\"\n", cmd);
		return;
	}

	MSG_WriteByte (&cls.netchan.message, clc_stringcmd);
	SZ_Print (&cls.netchan.message, cmd);
	if (Cmd_Argc() > 1)
	{
		SZ_Print (&cls.netchan.message, " ");
		SZ_Print (&cls.netchan.message, Cmd_Args());
	}
	cls.forcePacket = true;
}


/*
============
Cmd_ExecuteString

A complete command line has been parsed, so try to execute it
FIXME: lookupnoadd the token to speed search?
============
*/
void	Cmd_ExecuteString (char *text)
{
	cmd_function_t	*cmd;
	cmdalias_t		*a;

	Cmd_TokenizeString (text, true);

	// execute the command line
	if (!Cmd_Argc())
		return;		// no tokens

	unsigned	hash = Com_HashKey(cmd_argv[0]);
	// check functions
	for (cmd=cmd_functions ; cmd ; cmd=cmd->next)
	{
		if (hash == cmd->hash)
		{
			if (!Q_strcasecmp(cmd->name, cmd_argv[0]))
			{
				if (!cmd->function)
					Cmd_ExecuteString (va("cmd %s", text));	// forward to server command
				else
					cmd->function ();
				return;
			}
		}
	}

	// check alias
	for (a=cmd_alias ; a ; a=a->next)
	{
		if (hash == a->hash)
		{
			if (!Q_strcasecmp(a->name, cmd_argv[0]))
			{
				if (++alias_count == ALIAS_LOOP_COUNT)
				{
					Com_Printf ("^3ALIAS_LOOP_COUNT\n");
					return;
				}
				Cbuf_InsertText (a->value);
				return;
			}
		}
	}

	// check cvars
	if (Cvar_Command ())
		return;

	// send it as a server command if we are connected
	Cmd_ForwardToServer ();
}


void Cbuf_Execute ()
{
	int		i;
	char	*text;
	char	line[1024];
	int		quotes;

	alias_count = 0;		// don't allow infinite alias loops

	while (cmd_text.cursize)
	{
		if( cmd_wait != -1)
			if( cmd_wait && (cmd_wait > sys_time) )
				break;

// find a \n or ; line break
		text = (char *)cmd_text.data;

		quotes = 0;
		for (i=0 ; i< cmd_text.cursize ; i++)
		{
			if (text[i] == '"')
				quotes++;
			if ( !(quotes&1) &&  text[i] == ';')
				break;	// don't break if inside a quoted string
			if (text[i] == '\n')
				break;
		}

		memcpy (line, text, i);
		line[i] = 0;

// delete the text from the command buffer and move remaining commands down
// this is necessary because commands (exec, alias) can insert data at the
// beginning of the text buffer

		if (i == cmd_text.cursize)
			cmd_text.cursize = 0;
		else
		{
			i++;
			cmd_text.cursize -= i;
			memmove (text, text+i, cmd_text.cursize);
		}

// execute the command line
		Cmd_ExecuteString (line);

		if( cmd_wait == -1 )
		{
			// skip out while text still remains in buffer, leaving it
			// for next frame
			cmd_wait = 0;
			break;
		}
	}
}


