		savdir = "save";
	else
		savdir = "save.q2b";

	Com_DPrintf("SV_WriteLevelFile()\n");

	Com_sprintf (name, sizeof(name), "%s/%s/current/%s.sv2", FS_Gamedir(), savdir, sv.name);
