float ClampCvar( float min, float max, float value )
{
	if ( value < min ) return min;
	if ( value > max ) return max;
	return value;
}


int QualFromKHZ (int khz)
{
	if (khz == 48)
		return 3;
	else if (khz == 44)
		return 2;
	else if (khz == 22)
		return 1;
	else
		return 0;
}

