		if ((cont[0] & MASK_WATER) || (cont[1] & MASK_WATER) || (cont[2] & MASK_WATER) || (cont[3] & MASK_WATER)/* || (cont[4] & MASK_WATER)*/)
			in_water = true;
#else
		vec3_t	org;
		VectorSet(org, currententity->minmax[0], currententity->minmax[1], currententity->minmax[5]);
