	glDisable( GL_TEXTURE_2D );
	gl_TexEnv( GL_REPLACE );

	GL_SelectTexture( GL_TEXTURE0 );

	glPopMatrix ();

	GL_Color3f(1,1,1);

	// end occlusion test
