/*
Check if rectangles overlap
returns true if they overlap
*/
bool R_RectOverlap(screenrect_t *r1, screenrect_t *r2)
{
	//r1 is rightof r2
	if (r1->coords[0] > r2->coords[0])
	{
		//overlap in x dir?
		if ((r1->coords[0] - r2->coords[2]) < 0)
		{
			//check y dir

			//r1 below r2
			if (r1->coords[1] > r2->coords[1])
			{
				if ((r1->coords[1]-r2->coords[3]) < 0)
					return true;
				else
					return false;
			//r1 above r2
			}
			else
			{
				if ((r2->coords[1]-r1->coords[3]) < 0)
					return true;
				else
					return false;
			}
		//no overlap in x- they don't overlap at all
		}
		else
			return false;

		//r2 is rightof r1
	}
	else
	{
		//overlap in x dir
		if ((r2->coords[0] - r1->coords[2]) < 0)
		{
			//check y dir

			//r1 below r2
			if (r1->coords[1] > r2->coords[1])
			{
				if ((r1->coords[1]-r2->coords[3]) < 0)
					return true;
				else
					return false;
			//r1 above r2
			}
			else
			{
				if ((r2->coords[1]-r1->coords[3]) < 0)
					return true;
				else
					return false;
			}
		//no overlap in x- they don't overlap at all
		}
		else
			return false;
	}
}


bool R_CheckRectList(screenrect_t *rec)
{
	screenrect_t *r;
	r = recList;
	while (r)
	{
		if (R_RectOverlap(rec,r))
			return false;
		r = r->next;
	}
	return true;
}


