		return;

	glDisable (GL_TEXTURE_2D);
	glEnable (GL_BLEND);
	GL_BlendFunc (GL_ONE, GL_ONE);
///	glEnable(GL_POLYGON_OFFSET_FILL);
	glDepthMask( 0 );

