int Value2LifeTime(float val)
{
	if (!val)
		return 0;	// Very Quickly
	if (val <= 0.5)
		return 1;	// Quickly
	if (val <= 2)
		return 2;	// Normally
	if (val <= 5)
		return 3;	// Long
	if (val <= 20)
		return 4;	// Very long
	return 5;		// Eternally
}

float LifeTime2Value(int val)
{
	if (!val)
		return 0;	// Very Quickly
	if (val == 1)
		return 0.5;// Quickly
	if (val == 2)
		return 2;	// Normally
	if (val == 3)
		return 5;	// Long
	if (val == 4)
		return 20;	// Very long
	return 999999;	// Eternally
}

