const char *M_Main_Key (int key)
{
	const char *sound = menu_move_sound;

	switch (key)
	{
	case K_MOUSE2:
	case K_ESCAPE:
		M_PopMenu ();
		break;

	case K_MWHEELDOWN:
	case K_KP_DOWNARROW:
	case K_DOWNARROW:
		if (++m_main_cursor >= MAIN_ITEMS)
			m_main_cursor = 0;
		return (char*)sound;

	case K_MWHEELUP:
	case K_KP_UPARROW:
	case K_UPARROW:
		if (--m_main_cursor < 0)
			m_main_cursor = MAIN_ITEMS - 1;
		return (char*)sound;

	case K_MOUSE1:
	case K_KP_ENTER:
	case K_ENTER:
		m_entersound = true;

		switch (m_main_cursor)
		{
		case 0:
			M_Menu_Game_f ();
			break;

		case 1:
			M_Menu_Multiplayer_f();
			break;

		case 2:
			M_Menu_Demos_f ();
			break;

		case 3:
			M_Menu_Options_f ();
			break;

		case 4:
			M_Menu_Video_f ();
			break;

		case 5:
			M_Menu_Quit_f ();
			break;
		}
	}

	return NULL;
}


void M_Menu_Main_f ()
{
	M_PushMenu (M_Main_Draw, M_Main_Key);
}

void M_LoadMapNames()
{
	char	shortname[MAX_MAP_NAMELEN];
	char	scratch[200];
	int		i, j, l;

	FreeMapNames2();

	// load the list of map names
	num_maps = Create_Mapsstrings(3);

	map_names = (char **) Z_Malloc( sizeof( char * ) * ( num_maps + 1 ), true );
///	memset( map_names, 0, sizeof( char * ) * ( num_maps + 1 ) );

	for ( i = 0; i < num_maps; i++ )
	{
		l = strlen(m_maps[i]);
		for (j=0 ; j<l ; j++)
			shortname[j] = toupper(m_maps[i][j]);
		shortname[j] = 0;
		Com_sprintf( scratch, sizeof( scratch ), "%s", shortname );
		map_names[i] = (char *) Z_Malloc( strlen( scratch ) + 1, true );
		strcpy( map_names[i], scratch );
	}
	map_names[num_maps] = 0;

	qsort(map_names, num_maps, sizeof(char *), (int (*)(const void *,const void *))Q_SortStrcmp);
}


