		*g = 32;
		*b = 32;
		*r_ = 15;
		*g_ = 63;
		*b_ = 63;
		break;
	case 5:		/// пурпурный
		*r = 220;
		*g = 32;
		*b = 220;
		*r_ = 15;
		*g_ = 63;
		*b_ = 15;
		break;
	case 6:		/// желтый
