void CL_BubbleTrail (vec3_t start, vec3_t end, int type)
{
	vec3_t		move;
	vec3_t		vec;
	float		len;
	int			i, j;
	cparticle_t	*p;
	float		dec;
	byte		r, g, b;

	if(type==TE_BUBBLETRAIL_WATER)
	{
		r = 170;
		g = 170;
		b = 200;
	}
	else if(type==TE_BUBBLETRAIL_LAVA)
	{
		r = 200;
		g = 120;
		b = 120;
	}
	else
	{
		r = 120;
		g = 200;
		b = 120;
	}

	VectorCopy (start, move);
	VectorSubtract (end, start, vec);
	len = VectorNormalize (vec);

	dec = 32;
	VectorScale (vec, dec, vec);

	for (i=0 ; i<len ; i+=dec)
	{
		if (!free_particles)
			return;

		p = free_particles;
		free_particles = p->next;
		p->next = active_particles;
		active_particles = p;

		VectorClear (p->accel);
		p->flags = 0;
		p->time = cl.gameTime;

		p->alpha = 1.0;
		p->alphavel = -1.0 / (1+frand()*0.2);

		p->color_r0 = p->color_r1 = r + (rand()&31);
		p->color_g0 = p->color_g1 = g + (rand()&31);
		p->color_b0 = p->color_b1 = b + (rand()&31);

		for (j=0 ; j<3 ; j++)
		{
			p->lastOrg[j] = p->org[j] = move[j] + crand()*2;
			p->vel[j] = crand()*5;
		}
		p->vel[2] += 6;
		p->type = part_bubble;

		VectorAdd (move, vec, move);
	}
}


void CL_ParseBeam (struct model_s *model, bool large_map)
{
	int		ent;
	vec3_t	start, end;
	beam_t	*b;
	int		i;

	ent = MSG_ReadShort (&net_message);

	MSG_ReadPos (&net_message, start, large_map);
	MSG_ReadPos (&net_message, end, large_map);

// override any beam with the same entity
	for (i=0, b=cl_beams ; i< MAX_BEAMS ; i++, b++)
		if (b->entity == ent)
		{
			b->entity = ent;
			b->model = model;
			b->endtime = cl.time + 200;
			VectorCopy (start, b->start);
			VectorCopy (end, b->end);
			VectorClear (b->offset);
			return;/// ent;
		}

// find a free beam
	for (i=0, b=cl_beams ; i< MAX_BEAMS ; i++, b++)
	{
		if (!b->model || b->endtime < cl.time)
		{
			b->entity = ent;
			b->model = model;
			b->endtime = cl.time + 200;
			VectorCopy (start, b->start);
			VectorCopy (end, b->end);
			VectorClear (b->offset);
			return;/// ent;
		}
	}
	Com_Printf ("^3beam list overflow!\n");
	return;/// ent;
}


